import itertools

def simulate_befunge(program):
    # Simulate Befunge program
    # Returns True if the program can be executed without errors, False otherwise

    # Convert string into a 2D grid
    grid = [list(line) for line in program.split('\n')]

    height = len(grid)
    width = max(len(row) for row in grid) if height > 0 else 0

    # Initialize instruction pointer
    ip = [0, 0]  # [row, col]
    direction = [0, 1]  # Start moving right

    stack = []

    while True:
        r, c = ip
        if r < 0 or r >= height or c < 0 or c >= len(grid[r]):
            return False  # Out of bounds

        ch = grid[r][c]

        if ch == '>':
            direction = [0, 1]
        elif ch == '<':
            direction = [0, -1]
        elif ch == '^':
            direction = [-1, 0]
        elif ch == 'v':
            direction = [1, 0]
        elif ch == '+':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a + b)
        elif ch == '-':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a - b)
        elif ch == '*':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a * b)
        elif ch == '/':
            if len(stack) < 2:
                return False
            b = stack.pop()
            if b == 0:
                return False
            a = stack.pop()
            stack.append(a // b)
        elif ch == ':':
            if not stack:
                return False
            stack.append(stack[-1])
        elif ch == '.':
            if not stack:
                return False
            stack.pop()  # Output, don't need to do anything with the value
        elif ch == '_':
            if not stack:
                return False
            val = stack.pop()
            if val == 0:
                direction = [1, 0]  # Down
            else:
                direction = [-1, 0]  # Up
        elif ch == '|':
            if not stack:
                return False
            val = stack.pop()
            if val == 0:
                direction = [0, 1]  # Right
            else:
                direction = [0, -1]  # Left
        elif ch == '#':
            # Jump over next instruction
            ip[0] += direction[0]
            ip[1] += direction[1]
        elif ch == '@':
            # Halt
            return True
        elif ch.isdigit():
            stack.append(int(ch))
        elif ch == '"':
            # Read string literal
            pass  # Not handling string literals in this simplified version
        elif ch == ' ':
            # No-op
            pass

        # Move instruction pointer
        ip[0] += direction[0]
        ip[1] += direction[1]

    return False

