def run_befunge(program, input_digits):
    # Convert the string into a 2D grid
    grid = [list(line) for line in program.split('\n')]

    height = len(grid)
    width = len(grid[0]) if height > 0 else 0

    # Initial position: top-left corner, moving right
    x, y = 0, 0
    dx, dy = 1, 0  # direction: right

    # Stack and input handling
    stack = []
    input_index = 0
    input_length = len(input_digits)

    # Step counter to prevent infinite loops
    steps = 0
    max_steps = 10000

    while steps < max_steps:
        if x < 0 or x >= width or y < 0 or y >= height:
            return False  # exited the grid, not a valid input

        c = grid[y][x]

        if c == '>':
            dx, dy = 1, 0
        elif c == '<':
            dx, dy = -1, 0
        elif c == '^':
            dx, dy = 0, -1
        elif c == 'v':
            dx, dy = 0, 1
        elif c == '+':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a + b)
        elif c == '-':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a - b)
        elif c == '*':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a * b)
        elif c == '/':
            if len(stack) < 2:
                return False
            b = stack.pop()
            if b == 0:
                return False  # division by zero
            a = stack.pop()
            stack.append(a // b)
        elif c == '.':
            if stack:
                stack.pop()  # output, but we don't need to handle it
        elif c == ',':
            if input_index < input_length:
                stack.append(ord(input_digits[input_index]))
                input_index += 1
            else:
                return False  # no more input
        elif c == ':':
            if stack:
                stack.append(stack[-1])
            else:
                return False
        elif c == '_':
            if stack:
                val = stack.pop()
                if val == 0:
                    dx, dy = 1, 0
                else:
                    dx, dy = -1, 0
            else:
                return False
        elif c == '|':
            if stack:
                val = stack.pop()
                if val == 0:
                    dx, dy = 0, 1
                else:
                    dx, dy = 0, -1
            else:
                return False
        elif c == '?':
            # Random direction; we'll use a deterministic choice here for testing
            # For simplicity, pick a fixed direction (right)
            dx, dy = 1, 0
        elif c == '#':
            # Jump over next instruction
            x += dx
            y += dy
            continue
        elif c == '$':
            if stack:
                stack.pop()
            else:
                return False
        elif c == '@':
            return True  # program ends successfully

        x += dx
        y += dy
        steps += 1

    return False  # exceeded max steps


