from itertools import product

def run_befunge(program, input_digits):
    stack = []
    ip = 0  # Instruction pointer
    input_ptr = 0  # Pointer for input digits
    width = len(program)

    while ip < width:
        c = program[ip]

        if c == '?':
            if input_ptr >= len(input_digits):
                return False  # Not enough input
            stack.append(ord(input_digits[input_ptr]))
            input_ptr += 1
        elif c == '+':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a + b)
        elif c == '-':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a - b)
        elif c == '*':
            if len(stack) < 2:
                return False
            b = stack.pop()
            a = stack.pop()
            stack.append(a * b)
        elif c == '/':
            if len(stack) < 2:
                return False
            b = stack.pop()
            if b == 0:
                return False  # Division by zero
            a = stack.pop()
            stack.append(a // b)
        elif c == '>':
            ip += 1
        elif c == '<':
            ip -= 1
        elif c == 'v':
            ip += width
        elif c == '^':
            ip -= width
        elif c == '_':
            if stack and stack[-1] == 0:
                ip -= 1
            else:
                ip += 1
        elif c == '|':
            if stack and stack[-1] == 0:
                ip += 1
            else:
                ip -= 1
        elif c == '.':
            # Output the top of the stack, but we don't care about output
            if stack:
                stack.pop()
            else:
                return False
        else:
            # Ignore other characters or treat as no-op
            pass

        ip += 1  # Move to next character

    return True  # Program executed successfully

