import re
from collections import deque

def parse_golorp(name):
    # Parse the name to extract the constraints
    # Format: ?(expression):-condition>output.
    # We focus on the condition part and the output part
    match = re.match(r'\?$$([^$$]+)$$:-([^>]+)>([^<]+)\.', name)
    if not match:
        return None

    expression, condition, output = match.groups()

    # Parse condition: e.g., "___<__" -> [ (3, '<', 2) ]
    condition_parts = re.findall(r'(\d+)([<>=]+)(\d+)', condition)
    condition_constraints = []
    for a, op, b in condition_parts:
        a = int(a)
        b = int(b)
        condition_constraints.append((a, op, b))

    # Parse output: e.g., "___" -> [0, 1, 2]
    output_vars = [int(c) for c in output]

    # Parse expression: e.g., "___*__" -> [0, 1, 2, '*', 3, 4]
    expression_tokens = []
    i = 0
    while i < len(expression):
        if expression[i].isdigit():
            expression_tokens.append(int(expression[i]))
            i += 1
        elif expression[i] in '+-*/':
            expression_tokens.append(expression[i])
            i += 1
        else:
            i += 1

    return expression_tokens, condition_constraints, output_vars

def evaluate_expression(expr, values):
    stack = []
    i = 0
    while i < len(expr):
        token = expr[i]
        if isinstance(token, int):
            stack.append(values[token])
            i += 1
        elif token in '+-*/':
            b = stack.pop()
            a = stack.pop()
            if token == '+':
                stack.append(a + b)
            elif token == '-':
                stack.append(a - b)
            elif token == '*':
                stack.append(a * b)
            elif token == '/':
                if b == 0:
                    return None
                stack.append(a // b)
            i += 1
        else:
            i += 1
    return stack[0] if stack else None

def is_valid_sequence(seq, expr, condition_constraints):
    values = list(map(int, seq))
    result = evaluate_expression(expr, values)
    if result is None:
        return False

    for a, op, b in condition_constraints:
        val_a = values[a]
        val_b = values[b]
        if op == '<':
            if val_a >= val_b:
                return False
        elif op == '>':
            if val_a <= val_b:
                return False
        elif op == '=':
            if val_a != val_b:
                return False
    return True

def find_lex_smallest_sequence(expr, condition_constraints, output_vars):
    from itertools import product

    n = len(output_vars)
    for length in range(1, 10):  # Try lengths from 1 to 9
        for seq in product('0123456789', repeat=length):
            if is_valid_sequence(''.join(seq), expr, condition_constraints):
                # Check if the output variables match the sequence
                # This is a bit ambiguous; assuming output_vars are indices of the sequence
                if all(seq[i] == seq[output_vars[i]] for i in range(len(output_vars))):
                    return ''.join(seq)
    return None

