            left, right = c.split('<')
        else:
            continue

        left = int(left) if left.isdigit() else left
        right = int(right) if right.isdigit() else right
        parsed_constraints.append((op, left, right))

    # Generate all possible variable sequences based on the jaw pattern
    # '?' represents a variable, others are fixed values
    var_positions = [i for i, ch in enumerate(jaw_pattern) if ch == '?']
    num_vars = len(var_positions)

