import re
from collections import deque

def parse_golorp(name):
    # Split the name into parts
    parts = name.split(':')
    if len(parts) < 2:
        return None

    # Extract the pattern part
    pattern_part = parts[0]

    # Extract the constraints
    constraints = []
    if len(parts) > 1:
        constraints_str = parts[1]
        constraints = [c.strip() for c in constraints_str.split(',') if c.strip()]

    # Parse the pattern
    pattern = []
    i = 0
    while i < len(pattern_part):
        if pattern_part[i] == '?':
            # Variable
            pattern.append('var')
            i += 1
        elif pattern_part[i] == '(':
            # Start of a group
            j = i + 1
            while j < len(pattern_part) and pattern_part[j] != ')':
                j += 1
            if j < len(pattern_part):
                pattern.append('group')
                i = j + 1
            else:
                return None
        elif pattern_part[i] == ')':
            # End of a group
            pattern.append('endgroup')
            i += 1
        elif pattern_part[i] == '*':
            # Repeat previous element
            if pattern:
                pattern[-1] = 'repeat'
            i += 1
        elif pattern_part[i] == '+':
            # One or more of previous element
            if pattern:
                pattern[-1] = 'oneormore'
            i += 1
        elif pattern_part[i] == '-':
            # Subtract or negative
            pattern.append('minus')
            i += 1
        elif pattern_part[i] == '_':
            # Underscore (placeholder)
            pattern.append('underscore')
            i += 1
        elif pattern_part[i] == ':':
            # Colon
            pattern.append('colon')
            i += 1
        elif pattern_part[i] == '>':
            # Greater than
            pattern.append('greater')
            i += 1
        elif pattern_part[i] == '<':
            # Less than
            pattern.append('less')
            i += 1
        elif pattern_part[i] == '.':
            # End of pattern
            pattern.append('end')
            i += 1
        else:
            # Unknown character
            return None

    # Parse constraints
    constraint_map = {}
    for constraint in constraints:
        if '>' in constraint:
            lhs, rhs = constraint.split('>')
            lhs = lhs.strip()
            rhs = rhs.strip()
            if lhs in constraint_map:
                constraint_map[lhs].append(('>', rhs))
            else:
                constraint_map[lhs] = [('>', rhs)]
        elif '<' in constraint:
            lhs, rhs = constraint.split('<')
            lhs = lhs.strip()
            rhs = rhs.strip()
            if lhs in constraint_map:
                constraint_map[lhs].append(('<', rhs))
            else:
                constraint_map[lhs] = [('>', rhs)]

    return {
        'pattern': pattern,
        'constraints': constraint_map
    }

def evaluate_pattern(pattern, values):
    i = 0
    value_index = 0
    while i < len(pattern):
        if pattern[i] == 'var':
            if value_index >= len(values):
                return False
            i += 1
            value_index += 1
        elif pattern[i] == 'group':
            j = i + 1
            while j < len(pattern) and pattern[j] != 'endgroup':
                j += 1
            if j >= len(pattern):
                return False
            i = j + 1
        elif pattern[i] == 'endgroup':
            i += 1
        elif pattern[i] == 'repeat':
            if i > 0 and pattern[i - 1] == 'var':
                if value_index >= len(values):
                    return False
                value_index += 1
                i += 1
            else:
                return False
        elif pattern[i] == 'oneormore':
            if i > 0 and pattern[i - 1] == 'var':
                if value_index >= len(values):
                    return False
                value_index += 1
                i += 1
            else:
                return False
        elif pattern[i] == 'minus':
            i += 1
        elif pattern[i] == 'underscore':
            i += 1
        elif pattern[i] == 'colon':
            i += 1
        elif pattern[i] == 'greater':
            if value_index >= len(values):
                return False
            i += 1
        elif pattern[i] == 'less':
            if value_index >= len(values):
                return False
            i += 1
        elif pattern[i] == 'end':
            break
        else:
            return False
    return value_index == len(values)

def is_valid_sequence(sequence, constraints):
    for var, conds in constraints.items():
        if var not in sequence:
            return False
        val = int(sequence[var])
        for op, rhs in conds:
            if op == '>':
                if val <= int(rhs):
                    return False
            elif op == '<':
                if val >= int(rhs):
                    return False
    return True

def generate_lexicographic_sequences(length):
    from itertools import product
    return [''.join(map(str, p)) for p in product(range(10), repeat=length)]

def feed_golorp(name):
    parsed = parse_golorp(name)
    if not parsed:
        return "false"

    pattern = parsed['pattern']
    constraints = parsed['constraints']

    # Determine the number of variables needed
    var_count = 0
    for token in pattern:
        if token == 'var':
            var_count += 1
        elif token == 'repeat':
            if pattern and pattern[-1] == 'var':
                var_count += 1
        elif token == 'oneormore':
            if pattern and pattern[-1] == 'var':
                var_count += 1

    if var_count == 0:
        return "false"

    sequences = generate_lexicographic_sequences(var_count)
    for seq in sequences:
        if evaluate_pattern(pattern, seq) and is_valid_sequence(seq, constraints):
            return seq

    return "false"

