def count_zeros(n):
    count = 0
    while n >= 5:
        n //= 5
        count += n
    return count

def find_n_values(m):
    left = 0
    right = 5 * m
    # Find the lower bound where count_zeros == m
    while left < right:
        mid = (left + right) // 2
        if count_zeros(mid) < m:
            left = mid + 1
        else:
            right = mid
    lower = left
    if count_zeros(lower) != m:
        return []
    # Now find upper bound
    upper = lower
    while count_zeros(upper) == m:
        upper += 1
    return list(range(lower, upper))

