def main():
    import sys
    input = sys.stdin.read().split()
    n = int(input[0])
    s = input[1]

    def get_range(k, rules):
        if k == 0:
            # Only one team, no matches
            return (1, 2**0)
        else:
            # Recursively compute the range for the subtournament
            sub_lo, sub_hi = get_range(k-1, rules[:-1])
            current_rule = rules[-1]
            if current_rule == '1':
                # Need to be higher than the other winner
                # Other winner's range is [sub_lo, sub_hi], so x must be > sub_lo
                # So the new lo is sub_lo + 1, hi is 2^k
                new_lo = sub_lo + 1
                new_hi = 2**k
            else:
                # Need to be lower than the other winner
                # Other winner's range is [sub_lo, sub_hi], so x must be < sub_hi
                # So the new lo is 1, hi is sub_hi - 1
                new_lo = 1
                new_hi = sub_hi - 1
            return (new_lo, new_hi)

    lo, hi = get_range(n, s)
    result = []
    for x in range(lo, hi + 1):
        result.append(str(x))
    print(' '.join(result))

