    strings = [sys.stdin.readline().strip() for _ in range(2 * n - 2)]

    # Find the two strings of length n-1
    len_n_minus_1 = n - 1
    candidates_len_n_minus_1 = []
    for s in strings:
        if len(s) == len_n_minus_1:
            candidates_len_n_minus_1.append(s)

    # Generate possible candidate s
    possible_s = set()
    for a in candidates_len_n_minus_1:
        for b in candidates_len_n_minus_1:
            if a != b:
                # Check if a is prefix and b is suffix
                if b[:-1] == a[1:]:
                    candidate = a + b[-1]
                    possible_s.add(candidate)
                # Check if b is prefix and a is suffix
                if a[:-1] == b[1:]:
                    candidate = b + a[-1]
                    possible_s.add(candidate)

    # Now check each candidate to see if all input strings are prefixes or suffixes
    for candidate in possible_s:
        valid = True
        for s in strings:
            if not candidate.startswith(s) and not candidate.endswith(s):
                valid = False
                break
        if valid:
            # Found the candidate, now generate the answer
            prefix_list = [candidate[:i] for i in range(1, n)]
            suffix_list = [candidate[i:] for i in range(1, n)]
            prefix_counter = Counter(prefix_list)
            suffix_counter = Counter(suffix_list)

            result = []
            for s_input in strings:
                if prefix_counter[s_input] > 0:
                    result.append('P')
                    prefix_counter[s_input] -= 1
                else:
                    result.append('S')
                    suffix_counter[s_input] -= 1
            print(''.join(result))
            return

    # If no candidate found (though problem says it's guaranteed)
    # This part should not be reached as per problem statement
    print('')

