import sys
import heapq

def main():
    n, k = map(int, sys.stdin.readline().split())
    adj = [[] for _ in range(n + 1)]
    for _ in range(n - 1):
        a, b = map(int, sys.stdin.readline().split())
        adj[a].append(b)
        adj[b].append(a)

    in_component = [False] * (n + 1)
    heap = []

    start_node = n
    in_component[start_node] = True
    count = 1

    for neighbor in adj[start_node]:
        heapq.heappush(heap, -neighbor)

    while count < (n - k):
        current = -heapq.heappop(heap)
        if in_component[current]:
            continue
        in_component[current] = True
        count += 1
        for neighbor in adj[current]:
            if not in_component[neighbor]:
                heapq.heappush(heap, -neighbor)

    removed = []
    for i in range(1, n + 1):
        if not in_component[i]:
            removed.append(i)
    print(' '.join(map(str, sorted(removed))))

