import sys
import functools

MOD = 998244353

def main():
    import sys
    input = sys.stdin.read().split()
    idx = 0
    while idx < len(input):
        n = int(input[idx])
        k = int(input[idx+1])
        f = int(input[idx+2])
        idx += 3

        # Precompute powers of 2 up to n
        max_len = n
        max_power = 1 << (n)

        # Memoization cache for the DP function
        memo = {}

        # Define the DP function for a node at depth l (length l)
        def dp(l, depth):
            if (l, depth) in memo:
                return memo[(l, depth)]

            if l == n:
                # Leaf node: can contribute 0 or 1
                res = {}
                res[0] = 1  # c_s = 0
                res[1] = k  # c_s >= 1
                memo[(l, depth)] = res
                return res

            # Non-leaf node: has left and right children
            left_dp = dp(l + 1, depth + 1)
            right_dp = dp(l + 1, depth + 1)

            current_dp = {}

            # Iterate over all possible values from left and right
            for a in left_dp:
                for b in right_dp:
                    s = a + b
                    # Now, for current node, the possible v is min(c_s, s)
                    # So for each possible v, count how many ways
                    # For v <= s:
                    # If v < s: then c_s must be exactly v
                    # If v == s: then c_s >= s
                    # But also, c_s must be <=k

                    # For v = s:
                    if s > k:
                        cnt = 0
                    else:
                        cnt = (k + 1 - s) % MOD
                    if s not in current_dp:
                        current_dp[s] = 0
                    current_dp[s] = (current_dp[s] + left_dp[a] * right_dp[b] * cnt) % MOD

                    # For v < s:
                    if s >= 1:
                        for v in range(0, s):
                            if v > k:
                                continue
                            # c_s must be exactly v
                            if v not in current_dp:
                                current_dp[v] = 0
                            current_dp[v] = (current_dp[v] + left_dp[a] * right_dp[b] * 1) % MOD
            memo[(l, depth)] = current_dp
            return current_dp

        root_dp = dp(1, 1)
        result = 0
        for a in root_dp:
            for b in root_dp:
                if a + b == f:
                    result = (result + root_dp[a] * root_dp[b]) % MOD
        print(result)

