from collections import Counter

def is_mentsu(tiles):
    # Check for triplet
    if tiles[0] == tiles[1] == tiles[2]:
        return True
    # Check for sequence
    suit = tiles[0][1]
    if all(t[1] == suit for t in tiles):
        nums = sorted([int(t[0]) for t in tiles])
        if nums[1] == nums[0] + 1 and nums[2] == nums[1] + 1:
            return True
    return False

def compute_min_triplet_steps(tiles):
    original_counter = Counter(tiles)
    min_steps = float('inf')
    for n in range(1, 10):
        for s in ['m', 'p', 's']:
            tile = f"{n}{s}"
            count = original_counter.get(tile, 0)
            steps = 3 - count
            if steps < min_steps:
                min_steps = steps
    return min_steps

def compute_min_sequence_steps(tiles):
    min_steps = float('inf')
    for suit in ['m', 'p', 's']:
        current_numbers = []
        for t in tiles:
            if t[1] == suit:
                current_numbers.append(int(t[0]))
        current_set = set(current_numbers)
        for start in range(1, 8):
            required = {start, start+1, start+2}
            count = 0
            for num in required:
                if num in current_set:
                    count += 1
            steps_needed = 3 - count
            if steps_needed < min_steps:
                min_steps = steps_needed
    return min_steps

