    tiles = input().split()
    if is_mentsu(tiles):
        print(0)
    else:
        triplet_steps = compute_min_triplet_steps(tiles)
        sequence_steps = compute_min_sequence_steps(tiles)
        print(min(triplet_steps, sequence_steps))

