from itertools import combinations

def is_triplet(triplet):
    return triplet[0] == triplet[1] == triplet[2]

def is_sequence(triplet):
    suit = triplet[0][1]
    for t in triplet:
        if t[1] != suit:
            return False
    nums = sorted([int(t[0]) for t in triplet])
    return nums[2] - nums[0] == 2 and len(set(nums)) == 3

def has_mentsu(tiles):
    for triplet in combinations(tiles, 3):
        if is_triplet(triplet) or is_sequence(triplet):
            return True
    return False

