import sys

def count_triangles(A, B, C, D):
    total = 0
    for y in range(B, C + 1):
        z_max = min(D, B + y - 1)
        if z_max < C:
            continue
        threshold_z = A + y - 2
        z1_upper = min(z_max, threshold_z)
        count1 = max(0, z1_upper - C + 1)
        contribution1 = count1 * (B - A + 1)

        z2_lower = max(C, threshold_z + 1)
        if z2_lower > z_max:
            contribution2 = 0
        else:
            count2 = z_max - z2_lower + 1
            sum_z = (z2_lower + z_max) * count2 // 2
            contribution2 = (B + y) * count2 - sum_z

        total += contribution1 + contribution2
    return total

