            # The subarray from l to r (0-based), inclusive
            # When reversed, check how many 2->1 transitions are removed
            # Original transitions within [l, r] that are 2->1
            original_in = 0
            for i in range(l, r):
                if a[i] == 2 and a[i + 1] == 1:
                    original_in += 1

            # Also consider transitions between l-1 and l, and r and r+1
            # Check if the reversal affects these
            left_transition = 0
            if l > 0:
                if a[l - 1] == 2 and a[l] == 1:
                    left_transition = 1
            right_transition = 0
            if r < n - 1:
                if a[r] == 2 and a[r + 1] == 1:
                    right_transition = 1

            # After reversing, the new transitions at the boundaries
            new_left = 0
            if l > 0:
                if a[l - 1] == 2 and a[r] == 1:
                    new_left = 1
            new_right = 0
            if r < n - 1:
                if a[l] == 2 and a[r + 1] == 1:
                    new_right = 1

            # The number of transitions removed is original_in + left_transition + right_transition
            # Minus (new_left + new_right)
            total_removed = original_in + left_transition + right_transition - new_left - new_right

            max_remove = max(max_remove, total_removed)

    max_lnds = n - (transitions - max_remove)
    print(max_lnds)

