    power = [10**i for i in range(n)]

    @lru_cache(maxsize=None)
    def dp(h, m):
        if h == n and m == n:
            return 0
        current_pos = h + m
        current_digit = int(A[current_pos])
        options = []
        if h < n:
            contribution = current_digit * power[n - h - 1]
            next_val = dp(h + 1, m)
            options.append(contribution + next_val)
        if m < n:
            contribution = current_digit * power[n - m - 1]
            next_val = dp(h, m + 1)
            options.append(contribution + next_val)
        return max(options)

    # Backtracking to find the path
    h, m = 0, 0
    result = []
    while h + m < 2 * n:
        current_pos = h + m
        current_digit = int(A[current_pos])
        if h < n and m < n:
            option_h = current_digit * power[n - h - 1] + dp(h + 1, m)
            option_m = current_digit * power[n - m - 1] + dp(h, m + 1)
            if option_h == dp(h, m):
                result.append('H')
                h += 1
            else:
                result.append('M')
                m += 1
        elif h < n:
            result.append('H')
            h += 1
        else:
            result.append('M')
            m += 1

    print(''.join(result))

