def main():
    import sys
    input = sys.stdin.read
    n, mod = map(int, input().split())

    # Maximum number of inversions in a permutation of size n is n*(n-1)//2
    max_inv = n * (n - 1) // 2

    # Initialize DP table
    dp = [[0] * (max_inv + 1) for _ in range(n + 1)]
    dp[0][0] = 1  # Base case: empty permutation has 0 inversions

    for i in range(1, n + 1):
        for inv in range(max_inv + 1):
            for j in range(i):
                if inv >= j:
                    dp[i][inv] += dp[i - 1][inv - j]
                    dp[i][inv] %= mod

    # Compute the total number of pairs (p, q) where inv(p) > inv(q)
    total = 0
    for a in range(max_inv + 1):
        for b in range(a):
            total = (total + dp[n][a] * dp[n][b]) % mod

    # Since each such pair (p, q) with inv(p) > inv(q) has equal chance of being p < q or q < p
    # So divide by 2
    result = (total * pow(2, mod - 2, mod)) % mod
    print(result)

