def is_valid_string(s):
    # Check if all required characters are present
    if 'a' not in s or 'b' not in s or 'c' not in s:
        return "NO"

    # Check the order: all a's first, then b's, then c's
    # Find the first occurrence of 'b' and 'c'
    first_b = s.find('b')
    first_c = s.find('c')

    # All characters before first_b should be 'a's
    if first_b != -1:
        for ch in s[:first_b]:
            if ch != 'a':
                return "NO"

    # All characters between first_b and first_c should be 'b's
