    fibs = [1, 1]  # Fib(1) = 1, Fib(2) = 1
    while True:
        next_val = fibs[-1] + fibs[-2]
        if next_val > 10**18:
            break
        fibs.append(next_val)

    # Find the maximum m such that Fib(m) <= n
