import math

def compute_gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def compute_lcm(a, b):
    return a * b // compute_gcd(a, b)

def main():
    a, b = map(int, sys.stdin.readline().split())
    if a == b:
        print(0)
        return

    d = abs(a - b)
    min_k = None
    min_lcm = None

    # Iterate through all possible divisors of d
    for g in range(1, int(math.isqrt(d)) + 1):
        if d % g == 0:
            # Check both g and d//g
            for candidate_g in [g, d // g]:
                # Find the minimal x >= a such that x mod candidate_g == 0
                remainder = a % candidate_g
                if remainder == 0:
                    x = a
                else:
                    x = a + (candidate_g - remainder)
                # Compute the corresponding k
                k = x - a
                # Compute the LCM of x and x + d
                y = x + d
                current_lcm = compute_lcm(x, y)
                # Update the minimum
                if (min_lcm is None) or (current_lcm < min_lcm) or (current_lcm == min_lcm and k < min_k):
                    min_lcm = current_lcm
                    min_k = k

    # Also check the case when k=0
    k0 = 0
    x0 = a + k0
    y0 = b + k0
    lcm0 = compute_lcm(x0, y0)
    if (min_lcm is None) or (lcm0 < min_lcm) or (lcm0 == min_lcm and k0 < min_k):
        min_k = k0

    print(min_k)

