def find_min_k(a, b):
    if a == b:
        return 0
    diff = abs(a - b)
    # We want to make the two numbers as close as possible
    # So we try k = diff - d where d divides diff
    # The optimal k is diff - d where d is the largest divisor of diff less than diff
    # That makes the two numbers equal or as close as possible
    for d in range(diff - 1, 0, -1):
        if diff % d == 0:
            return diff - d
    return diff - 1

