import math

def gcd(x, y):
    while y:
        x, y = y, x % y
    return x

def lcm(x, y):
    return x * y // gcd(x, y)

def find_min_k(a, b):
    if a == b:
        return 0
    a, b = sorted((a, b))
    d = b - a
    for k in range(d):
        val = lcm(a + k, b + k)
        if k == 0:
            min_lcm = val
            best_k = k
        else:
            if val < min_lcm or (val == min_lcm and k < best_k):
                min_lcm = val
                best_k = k
    return best_k

