from collections import defaultdict

def longest_substring_twice(s):
    n = len(s)
    seen = defaultdict(set)

    # Try all possible substring lengths from n-1 down to 1
    for length in range(n - 1, 0, -1):
        for i in range(n - length + 1):
            substr = s[i:i+length]
            if substr in seen:
                return length
            seen[substr].add(i)
    return 0

