    zeros = []
    twos = []
    ones = []

    for c in s:
        if c == '0':
            zeros.append(c)
        elif c == '2':
            twos.append(c)
        else:
            ones.append(c)

    # Now, build the result by interleaving the zeros and twos with the ones
    result = []
    z_idx = 0
    t_idx = 0
    for c in s:
        if c == '0':
            result.append(zeros[z_idx])
            z_idx += 1
        elif c == '2':
            result.append(twos[t_idx])
            t_idx += 1
        else:
            # For '1's, we need to add them in sorted order (all '1's are same, so just append)
            result.append('1')
    return ''.join(result)

