def min_ternary_string(s):
    # We can swap '0' and '1', and '1' and '2', but not '0' and '2'
    # So, we can rearrange the characters in groups of 0s, 1s, and 2s
    # But the relative order between 0s and 2s is fixed (since we cannot swap them directly)
    # So, we can move all 0s to the left, then 1s, then 2s, but with some constraints

    # Let's collect the positions of 0s, 1s, and 2s
    zeros = []
    ones = []
    twos = []

    for i, ch in enumerate(s):
        if ch == '0':
            zeros.append(i)
        elif ch == '1':
            ones.append(i)
        else:
            twos.append(i)

    # We will construct the result string
    res = list(s)

    # Move all '0's to the left as much as possible
    # We can only move '0's past '1's, not '2's
    # So we will replace the first len(zeros) positions with '0's
    # But we need to preserve the relative order of '0's and '2's

    # First, fill in all '0's
    z_idx = 0
    for i in range(len(s)):
        if z_idx < len(zeros):
            res[i] = '0'
            z_idx += 1
        else:
            break

    # Now, fill in all '1's
    o_idx = 0
    for i in range(len(s)):
        if o_idx < len(ones):
            res[i] = '1'
            o_idx += 1
        else:
            break

    # Finally, fill in all '2's
    t_idx = 0
    for i in range(len(s)):
        if t_idx < len(twos):
            res[i] = '2'
            t_idx += 1
        else:
            break

    return ''.join(res)

