def ccw(p, q, r):
    val = (q[0] - p[0]) * (r[1] - p[1]) - (q[1] - p[1]) * (r[0] - p[0])
    if val > 0:
        return 1
    elif val < 0:
        return -1
    else:
        return 0

def segments_intersect(p1, q1, p2, q2):
    o1 = ccw(p1, q1, p2)
    o2 = ccw(p1, q1, q2)
    o3 = ccw(p2, q2, p1)
    o4 = ccw(p2, q2, q1)
    return (o1 != o2) and (o3 != o4)

