def ccw(A, B, C):
    return (B[0] - A[0]) * (C[1] - A[1]) - (B[1] - A[1]) * (C[0] - A[0])

def segments_intersect(A, B, C, D):
    ccw1 = ccw(A, B, C)
    ccw2 = ccw(A, B, D)
    ccw3 = ccw(C, D, A)
    ccw4 = ccw(C, D, B)
    return (ccw1 * ccw2 < 0) and (ccw3 * ccw4 < 0)

def main():
    input_lines = [line.strip() for line in sys.stdin if line.strip()]
    ptr = 0
    R, B = map(int, input_lines[ptr].split())
    ptr += 1
    ships = []
    for _ in range(R):
        x, y = map(int, input_lines[ptr].split())
        ships.append((x, y))
        ptr += 1
    bases = []
    for _ in range(B):
        x, y = map(int, input_lines[ptr].split())
        bases.append((x, y))
        ptr += 1
    if R != B:
        print("No")
        return
    for perm in permutations(bases):
        valid = True
        for i in range(R):
            for j in range(i + 1, R):
                A = ships[i]
                B_point = perm[i]
                C = ships[j]
                D_point = perm[j]
                if segments_intersect(A, B_point, C, D_point):
                    valid = False
                    break
            if not valid:
                break
        if valid:
            print("Yes")
            return
    print("No")

if __name__ == "__main__":
