import sys
import itertools

def read_input():
    data = sys.stdin.read().split('\n')
    lines = [line.strip() for line in data if line.strip()]
    R, B = map(int, lines[0].split())
    rebels = [tuple(map(int, lines[i+1].split())) for i in range(R)]
    bases = [tuple(map(int, lines[i+1+R].split())) for i in range(B)]
    return R, B, rebels, bases

def orientation(p, q, r):
    # Returns 0 if colinear, 1 if clockwise, 2 if counterclockwise
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0
    return 1 if val > 0 else 2

def on_segment(p, q, r):
    return (min(p[0], r[0]) <= q[0] <= max(p[0], r[0]) and
            min(p[1], r[1]) <= q[1] <= max(p[1], r[1]))

def segments_intersect(seg1, seg2):
    p1, q1 = seg1
    p2, q2 = seg2
    o1 = orientation(p1, q1, p2)
    o2 = orientation(p1, q1, q2)
    o3 = orientation(p2, q2, p1)
    o4 = orientation(p2, q2, q1)

    if o1 != o2 and o3 != o4:
        return True

    if o1 == 0 and on_segment(p1, p2, q1):
        return True
    if o2 == 0 and on_segment(p1, q2, q1):
        return True
    if o3 == 0 and on_segment(p2, p1, q2):
        return True
    if o4 == 0 and on_segment(p2, q1, q2):
        return True

    return False

def is_valid_matching(rebels, bases, matching):
    # Create list of segments from matching
    segments = [(rebels[i], bases[j]) for i, j in enumerate(matching)]
    # Check all pairs of segments for intersection
    for i in range(len(segments)):
        for j in range(i + 1, len(segments)):
            if segments_intersect(segments[i], segments[j]):
                return False
    return True

def solve():
    R, B, rebels, bases = read_input()
    if R != B:
        print("No")
        return

    # Try all possible perfect matchings
    for perm in itertools.permutations(range(B)):
        if is_valid_matching(rebels, bases, perm):
            print("Yes")
            return
    print("No")

if __name__ == "__main__":
    solve()

