def read_input():
    data = sys.stdin.read().split('\n')
    R, B = map(int, data[0].split())
    rebels = []
    for i in range(1, R + 1):
        x, y = map(int, data[i].split())
        rebels.append((x, y))
    bases = []
    for i in range(R + 1, R + B + 1):
        x, y = map(int, data[i].split())
        bases.append((x, y))
    return R, B, rebels, bases

def ccw(A, B, C):
    return (B[0] - A[0]) * (C[1] - A[1]) - (B[1] - A[1]) * (C[0] - A[0])

def segments_intersect(A, B, C, D):
    # Check if line segments AB and CD intersect
    ccw1 = ccw(A, B, C)
    ccw2 = ccw(A, B, D)
    ccw3 = ccw(C, D, A)
    ccw4 = ccw(C, D, B)

    if (ccw1 * ccw2 < 0) and (ccw3 * ccw4 < 0):
        return True
    return False

def is_valid_matching(rebels, bases):
    # Try all permutations of bases to match with rebels
    for perm in itertools.permutations(bases):
        valid = True
        for r, b in zip(rebels, perm):
            for r2, b2 in zip(rebels, perm):
                if r == r2 or b == b2:
                    continue
                if segments_intersect(r, b, r2, b2):
                    valid = False
                    break
            if not valid:
                break
        if valid:
            return True
    return False

def main():
    R, B, rebels, bases = read_input()
    if R != B:
        print("No")
        return
    if is_valid_matching(rebels, bases):
        print("Yes")
    else:
        print("No")

