import sys
import itertools

def read_input():
    data = sys.stdin.read().split('\n')
    lines = [line.strip() for line in data if line.strip()]
    R, B = map(int, lines[0].split())
    rebels = []
    for i in range(1, R + 1):
        x, y = map(int, lines[i].split())
        rebels.append((x, y))
    bases = []
    for i in range(R + 1, R + B + 1):
        x, y = map(int, lines[i].split())
        bases.append((x, y))
    return R, B, rebels, bases

def ccw(A, B, C):
    return (B[0] - A[0]) * (C[1] - A[1]) - (B[1] - A[1]) * (C[0] - A[0])

def segments_intersect(A, B, C, D):
    # Check if segment AB intersects with segment CD
    ccw1 = ccw(A, B, C)
    ccw2 = ccw(A, B, D)
    ccw3 = ccw(C, D, A)
    ccw4 = ccw(C, D, B)

    if ((ccw1 > 0 and ccw2 < 0) or (ccw1 < 0 and ccw2 > 0)) and ((ccw3 > 0 and ccw4 < 0) or (ccw3 < 0 and ccw4 > 0)):
        return True
    return False

def is_valid_matching(rebels, bases, matching):
    # Check if any two segments in the matching intersect
    for i in range(len(matching)):
        r1, b1 = rebels[matching[i][0]], bases[matching[i][1]]
        for j in range(i + 1, len(matching)):
            r2, b2 = rebels[matching[j][0]], bases[matching[j][1]]
            if segments_intersect(r1, b1, r2, b2):
                return False
    return True

def main():
    R, B, rebels, bases = read_input()
    if R != B:
        print("No")
        return

    # Try all possible perfect matchings
    for perm in itertools.permutations(range(B)):
        matching = [(i, perm[i]) for i in range(R)]
        if is_valid_matching(rebels, bases, matching):
            print("Yes")
            return
    print("No")

if __name__ == "__main__":
    main()

import sys

def main():
    import sys
    input = sys.stdin.read
    data = input().split('\n')

    # Parse input
    n, x = map(int, data[0].split())
    a = list(map(int, data[1].split()))
    b = list(map(int, data[2].split()))

    # Sort the scores
    a_sorted = sorted(a)
    b_sorted = sorted(b)

    # Best place: maximize Vasya's total
    max_vasya = 0
    for i in range(n):
        for j in range(n):
            if a_sorted[i] + b_sorted[j] >= x:
                max_vasya = a_sorted[i] + b_sorted[j]
                break
        if max_vasya != 0:
            break

    # Count how many participants can have total >= max_vasya
    count_best = 0
    for ai in a_sorted:
        for bj in b_sorted:
            if ai + bj >= max_vasya:
                count_best += 1
                break

    # Worst place: minimize Vasya's total
    min_vasya = 0
    for i in range(n):
        for j in range(n):
            if a_sorted[i] + b_sorted[j] >= x:
                min_vasya = a_sorted[i] + b_sorted[j]
                break
        if min_vasya != 0:
            break

    # Count how many participants can have total > min_vasya
    count_worst = 0
    for ai in a_sorted:
        for bj in b_sorted:
            if ai + bj > min_vasya:
                count_worst += 1
                break

    best_place = count_best + 1
    worst_place = count_worst + 1

    print(f"{best_place} {worst_place}")

