import sys
import threading
from collections import deque

def main():
    sys.setrecursionlimit(1 << 25)
    n, m = map(int, sys.stdin.readline().split())
    edges = [[] for _ in range(n)]
    for _ in range(m):
        u, v = map(int, sys.stdin.readline().split())
        edges[u-1].append(v-1)
        edges[v-1].append(u-1)

    is_bipartite = True
    color = [-1] * n
    components = []
    for start in range(n):
        if color[start] == -1:
            queue = deque()
            queue.append(start)
            color[start] = 0
            cnt0 = 1
            cnt1 = 0
            while queue:
                u = queue.popleft()
                for v in edges[u]:
                    if color[v] == -1:
                        color[v] = color[u] ^ 1
                        if color[v] == 0:
                            cnt0 += 1
                        else:
                            cnt1 += 1
                        queue.append(v)
                    else:
                        if color[v] == color[u]:
                            is_bipartite = False
            components.append((cnt0, cnt1))

    if not is_bipartite:
        print("0 1")
        return

    min_t = float('inf')
    ways = 0
    for (a, b) in components:
        if a == 0 or b == 0:
            continue
        size = min(a, b)
        if size > 0:
            if min_t > 1:
                min_t = 1
                ways = 0
            if min_t == 1:
                ways += a * b
        else:
            if min_t > 2:
                min_t = 2
                ways = 0
            if min_t == 2:
                ways += (a + b) * (a + b - 1) // 2

    if min_t == float('inf'):
        # All components are trees or isolated nodes
        # Need to connect three nodes
        min_t = 3
        ways = 1
    elif min_t == 1:
        pass
    elif min_t == 2:
        pass

    print(f"{min_t} {ways}")

