    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    m = int(input[idx])
    idx += 1

    adj = [[] for _ in range(n + 1)]
    for _ in range(m):
        u = int(input[idx])
        idx += 1
        v = int(input[idx])
        idx += 1
        adj[u].append(v)
        adj[v].append(u)

    colors = [-1] * (n + 1)
    is_bipartite = True
    components = []

    for u in range(1, n + 1):
        if colors[u] == -1:
            queue = deque()
            queue.append(u)
            colors[u] = 0
            a = 1
            b = 0
            while queue:
                current = queue.popleft()
                for neighbor in adj[current]:
                    if colors[neighbor] == -1:
                        colors[neighbor] = 1 - colors[current]
                        if colors[neighbor] == 0:
                            a += 1
                        else:
                            b += 1
                        queue.append(neighbor)
                    else:
                        if colors[neighbor] == colors[current]:
                            is_bipartite = False
            components.append((a, b))

    if not is_bipartite:
        print("0 1")
        return

    has_large_component = False
    for a, b in components:
        if a + b >= 2:
            has_large_component = True
            break

    if has_large_component:
        total_ways = 0
        for a, b in components:
            total_ways += (a * (a - 1)) // 2 + (b * (b - 1)) // 2
        print(f"1 {total_ways}")
    else:
        if n >= 3:
            w = n * (n - 1) * (n - 2) // 6
            print(f"3 {w}")
        else:
            print("0 0")  # This case should not occur as per problem constraints

if __name__ == "__main__":
    main()

import sys
from collections import deque

def main():
    input = sys.stdin.read
    data = input().split('\n')

    idx = 0
    while idx < len(data) and data[idx].strip() == '':
        idx += 1
    if idx >= len(data):
        print("0 1")
        return

    n, m = map(int, data[idx].split())
    idx += 1

    adj = [[] for _ in range(n)]

    for _ in range(m):
        while idx < len(data) and data[idx].strip() == '':
            idx += 1
        if idx >= len(data):
            break
        a, b = map(int, data[idx].split())
        adj[a - 1].append(b - 1)
        adj[b - 1].append(a - 1)
        idx += 1

    # Check if the graph already contains an odd-length cycle
    is_bipartite = [True] * n
    color = [-1] * n

    def bfs(start):
        queue = deque()
        queue.append(start)
        color[start] = 0
        while queue:
            u = queue.popleft()
            for v in adj[u]:
                if color[v] == -1:
                    color[v] = color[u] ^ 1
                    queue.append(v)
                elif color[v] == color[u]:
                    return False
        return True

    has_odd_cycle = False
    for i in range(n):
        if color[i] == -1:
            if not bfs(i):
                has_odd_cycle = True
                break

    if has_odd_cycle:
        print("0 1")
        return

    # If no odd cycle exists, the graph is bipartite
    # To form an odd cycle, we need to connect two nodes in the same bipartition
    # So count the number of nodes in each partition for each connected component

    from collections import defaultdict

    components = []
    visited = [False] * n

    for i in range(n):
        if not visited[i]:
            color = [-1] * n
            queue = deque([i])
            color[i] = 0
            visited[i] = True
            cnt0, cnt1 = 0, 0
            while queue:
                u = queue.popleft()
                if color[u] == 0:
                    cnt0 += 1
                else:
                    cnt1 += 1
                for v in adj[u]:
                    if not visited[v]:
                        visited[v] = True
                        color[v] = color[u] ^ 1
                        queue.append(v)
            components.append((cnt0, cnt1))

    total_ways = 0
    min_edges = float('inf')

    for cnt0, cnt1 in components:
        ways = cnt0 * cnt1
        total_ways += ways
        if ways > 0:
            min_edges = 1  # Only one edge needed to create an odd cycle

    print(f"{min_edges} {total_ways}")

if __name__ == "__main__":
    main()

MOD = 10**9 + 7

def main():
    import sys
    input = sys.stdin.read().split()
    m = int(input[0])
    operations = input[1:m+1]

    # Set of invalid codes
    invalid = {"0011", "0101", "1110", "1111"}

    # dp[i] is the total number of valid sequences considering the first i characters
    dp = [0] * (m + 1)
    S = []

    for i in range(1, m + 1):
        S.append(operations[i-1])
        current = ''.join(S)
        dp[i] = dp[i-1]  # Start with previous total

        # Check for substrings ending at i with lengths 1 to 4
        for l in range(1, 5):
            if i - l >= 0:
                substring = current[-l:]
                if substring not in invalid:
                    if l == 1:
                        dp[i] = (dp[i] + 1) % MOD
                    else:
                        dp[i] = (dp[i] + dp[i - l]) % MOD
        print(dp[i] % MOD)

