    for op in ops:
        S.append(op)
        current_len = len(S)
        # Check all possible substrings ending at current_len
        # with lengths 1 to 4
        for l in range(1, 5):
            if current_len - l >= 0:
                substring = ''.join(S[current_len - l:current_len])
                if substring not in invalid:
                    total += 1
        print(total % MOD)

