def main():
    import sys
    input = sys.stdin.read().split()
    m = int(input[0])
    operations = input[1:m+1]

    S = []
    dp = [0] * (m + 1)  # dp[i] is the number of valid sequences ending at position i
    prefix = [0] * (m + 1)  # prefix[i] is the total number of sequences up to position i

    for i in range(1, m + 1):
        c = operations[i - 1]
        S.append(c)
        current_len = len(S)

        # Check all possible lengths from 1 to 4
        for l in range(1, 5):
            if current_len - l < 0:
                continue
            substring = ''.join(S[current_len - l:])
            if substring in valid:
                if l == 1:
                    dp[i] = (dp[i] + 1) % MOD
                else:
                    dp[i] = (dp[i] + prefix[current_len - l]) % MOD

        # Update prefix sum
        prefix[i] = (prefix[i - 1] + dp[i]) % MOD

        print(prefix[i] % MOD)

