MOD = 10**9 + 7

def main():
    import sys
    input = sys.stdin.read().split()
    m = int(input[0])
    ops = input[1:m+1]

    # Precompute valid morse codes
    invalid = {"0011", "0101", "1110", "1111"}
    valid = set()
    for l in range(1, 5):
        for bits in range(2**l):
            s = bin(bits)[2:].zfill(l)
            if s not in invalid:
                valid.add(s)

    s = []
    dp = [0] * (m + 1)  # dp[i] is the number of ways for substrings ending at i-th step
    total = 0
    ans = []

    for i in range(m):
        s.append(ops[i])
        current = ''.join(s)
        # For the current step, we need to compute the contribution to the total
        # We'll look back up to 4 steps
        contrib = 0
        for l in range(1, 5):
            if i - l + 1 < 0:
                continue
            substr = current[-l:]
            if substr in valid:
                if i - l == -1:
                    contrib += 1
                else:
                    contrib += dp[i - l + 1]
                contrib %= MOD
        dp[i + 1] = contrib
        total = (total + contrib) % MOD
        ans.append(str(total))

    print('\n'.join(ans))

if __name__ == "__main__":
    main()

import sys

MOD = 10**9 + 7

# Valid Morse codes (length 1 to 4, excluding "0011", "0101", "1110", "1111")
valid_morse = set()
for length in range(1, 5):
    for bits in range(2**length):
        code = bin(bits)[2:].zfill(length)
        if code not in ["0011", "0101", "1110", "1111"]:
            valid_morse.add(code)

def main():
    m = int(sys.stdin.readline())
    S = ""
    dp = [0] * 5  # dp[i] = number of valid Morse codes ending at position i-1
    total = 0
    for _ in range(m):
        c = sys.stdin.readline().strip()
        S += c
        # Update dp array
        for i in range(4, 0, -1):
            if len(S) >= i:
                substr = S[-i:]
                if substr in valid_morse:
                    dp[i] = (dp[i] + 1 + dp[i-1]) % MOD
                else:
                    dp[i] = dp[i-1]
            else:
                dp[i] = dp[i-1]
        total = sum(dp) % MOD
        print(total)

if __name__ == "__main__":
    main()

