import sys
import math

def compute_gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def compute_lcm(a, b):
    return a * b // compute_gcd(a, b)

def determine_outcome(a, b):
    if a == b:
        return (0, 0)
    if (a == 'R' and b == 'S') or (a == 'S' and b == 'P') or (a == 'P' and b == 'R'):
        # Nikephoros wins, Polycarpus loses
        return (0, 1)
    else:
        # Polycarpus wins, Nikephoros loses
        return (1, 0)

def main():
    input = sys.stdin.read().split()
    n = int(input[0])
    A = input[1]
    B = input[2]

    m = len(A)
    k = len(B)

    T = compute_lcm(m, k)

    # Precompute the number of losses in one full period
    total_nik = 0
    total_poly = 0

    for i in range(T):
        a = A[i % m]
        b = B[i % k]
        nik, poly = determine_outcome(a, b)
        total_nik += nik
        total_poly += poly

    # Number of full periods and remainder
    q = n // T
    r = n % T

    # Total from full periods
    total_nik_full = total_nik * q
    total_poly_full = total_poly * q

    # Compute the remaining rounds
    for i in range(r):
        a = A[i % m]
        b = B[i % k]
        nik, poly = determine_outcome(a, b)
        total_nik_full += nik
        total_poly_full += poly

    print(f"{total_nik_full} {total_poly_full}")

