def main():
    import sys
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    l_str = input[idx]
    idx += 1
    r_str = input[idx]
    idx += 1

    l = list(l_str)
    r = list(r_str)

    # Step 1: Pair specific colors first
    from collections import defaultdict

    # Count for each color on left and right (excluding '?')
    left_color_count = defaultdict(int)
    right_color_count = defaultdict(int)
    left_indices = {}
    right_indices = {}

    # Prepare lists for each color (excluding '?')
    for i in range(n):
        if l[i] != '?':
            left_color_count[l[i]] += 1
            if l[i] not in left_indices:
                left_indices[l[i]] = []
            left_indices[l[i]].append(i)
        if r[i] != '?':
            right_color_count[r[i]] += 1
            if r[i] not in right_indices:
                right_indices[r[i]] = []
            right_indices[r[i]].append(i)

    # Now, for each color, pair as many as possible
    pairs = []
    used_left = [False] * n
    used_right = [False] * n

    for color in left_color_count:
        if color in right_color_count:
            min_count = min(left_color_count[color], right_color_count[color])
            if min_count > 0:
                # Take min_count from left and right
                left_list = left_indices[color]
                right_list = right_indices[color]
                for i in range(min_count):
                    left_idx = left_list[i]
                    right_idx = right_list[i]
                    pairs.append((left_idx + 1, right_idx + 1))
                    used_left[left_idx] = True
                    used_right[right_idx] = True

    # Now collect remaining left and right boots (including '?')
    remaining_left = []
    remaining_right = []

    for i in range(n):
        if not used_left[i]:
            remaining_left.append(i)
        if not used_right[i]:
            remaining_right.append(i)

    # Now handle the remaining boots with '?'
    # Collect left and right question marks
    left_q = []
    right_q = []
    left_other = []
    right_other = []

    for i in remaining_left:
        if l[i] == '?':
            left_q.append(i)
        else:
            left_other.append(i)
