def is_snowflake(n):
    if n == 1:
        return False
    # Try m from 1 to 60 (sufficient since k^m grows very fast)
    for m in range(1, 61):
        # Find k such that (k^(m+1) - 1) / (k - 1) == n
        low = 2
        high = int(n ** (1.0 / (m + 1))) + 2
        while low <= high:
            mid = (low + high) // 2
            # Compute the sum: 1 + mid + mid^2 + ... + mid^m
            total = 0
