import sys
import re

def parse_golorp(name):
    # Extract the part that defines the jaw structure
    jaw_part = re.search(r'\(([^)]+)\)', name).group(1)
    # Extract the part that defines the feeding direction
    direction_part = re.search(r'[:-]+', name).group()

    # Determine the direction: '>' means right, '<' means left
    direction = direction_part[0]

    # Split the jaw into segments
    segments = jaw_part.split('/')

    # Build a list of operations for each segment
    operations = []
    for seg in segments:
        if '+' in seg:
            ops = seg.split('+')
            for op in ops:
                if op:
                    operations.append(('add', int(op)))
        elif '-' in seg:
            ops = seg.split('-')
            for op in ops:
                if op:
                    operations.append(('subtract', int(op)))
        elif '*' in seg:
            ops = seg.split('*')
            for op in ops:
                if op:
                    operations.append(('multiply', int(op)))
        elif '/' in seg:
            ops = seg.split('/')
            for op in ops:
                if op:
                    operations.append(('divide', int(op)))
        else:
            operations.append(('constant', int(seg)))

    return operations, direction

def simulate_feeding(operations, direction):
    # Initialize the value to be fed
    value = 0
    result = []

    # Simulate the feeding process based on the direction
    if direction == '>':
        for op, num in operations:
            if op == 'add':
                value += num
            elif op == 'subtract':
                value -= num
            elif op == 'multiply':
                value *= num
            elif op == 'divide':
                if num == 0:
                    return None
                value //= num
            elif op == 'constant':
                value = num
            result.append(str(value % 10))
    elif direction == '<':
        for op, num in reversed(operations):
            if op == 'add':
                value += num
            elif op == 'subtract':
                value -= num
            elif op == 'multiply':
                value *= num
            elif op == 'divide':
                if num == 0:
                    return None
                value //= num
            elif op == 'constant':
                value = num
            result.append(str(value % 10))

    # Reverse the result to get the correct order for '<' direction
    if direction == '<':
        result = result[::-1]

    return ''.join(result)

def find_lex_smallest_sequence(operations, direction):
    # Try all possible starting values from 0 to 9
    for start in range(10):
        value = start
        result = [str(start)]
        valid = True

        # Simulate the feeding process
        for op, num in operations:
            if op == 'add':
                value += num
            elif op == 'subtract':
                value -= num
            elif op == 'multiply':
                value *= num
            elif op == 'divide':
                if num == 0:
                    valid = False
                    break
                value //= num
            elif op == 'constant':
                value = num
            result.append(str(value % 10))

        if valid:
            # Check if the final value is 0 (as per the example)
            if value % 10 == 0:
                return ''.join(result)

    return None

def main():
    input_str = sys.stdin.read().strip()
    names = input_str.split('\n')

    for name in names:
        operations, direction = parse_golorp(name)
        sequence = find_lex_smallest_sequence(operations, direction)
        print(sequence if sequence else 'false')

