import sys
import re

def parse_golorp(name):
    # Extract the part that defines the jaw structure
    jaw_part = re.search(r'\(([^)]+)\)', name).group(1)
    # Extract the part that defines the feeding direction
    direction_part = re.search(r':-([^<>.]+)([<>.])', name).group(2)

    # Determine the direction of feeding
    if direction_part == '>':
        direction = 1
    elif direction_part == '<':
        direction = -1
    else:
        return "false"  # Should not happen as per problem statement

    # Split the jaw structure into segments
    segments = re.split(r'([*+-/])', jaw_part)
    # Remove empty strings
    segments = [s for s in segments if s]

    # Build a list of operations and operands
    operations = []
    operands = []
    i = 0
    while i < len(segments):
        if segments[i] in '+-*/':
            operations.append(segments[i])
            i += 1
        else:
            operands.append(segments[i])
            i += 1

    # Check if the number of operands matches the expected pattern
    if len(operands) != len(operations) + 1:
        return "false"

    # Now, we need to find the lexicographically smallest sequence of digits
    # such that the expression evaluates to zero.
    # We'll use backtracking with pruning to find the minimal solution.

    def evaluate(expr):
        try:
            return eval(expr)
        except:
            return None

    from itertools import product

    # Try all possible digit sequences
    for length in range(1, len(operands) + 1):
        for digits in product('0123456789', repeat=length):
            expr = operands[0]
            for i in range(len(operations)):
                expr += operations[i] + digits[i]
            expr += operands[-1]
            result = evaluate(expr)
            if result == 0:
                return ''.join(digits)
    return "false"

