import sys
from collections import deque

def read_input():
    input = sys.stdin.read().split('\n')
    x1, y1 = map(int, input[0].split())
    x2, y2 = map(int, input[1].split())
    n = int(input[2])
    roads = []
    for i in range(3, 3 + n):
        if input[i]:
            a, b, c = map(int, input[i].split())
            roads.append((a, b, c))
    return x1, y1, x2, y2, roads

def sign(x, y, a, b, c):
    return a * x + b * y + c

def get_signs(point, roads):
    x, y = point
    signs = []
    for a, b, c in roads:
        s = sign(x, y, a, b, c)
        signs.append(1 if s > 0 else -1)
    return tuple(signs)

def bfs(start_signs, end_signs, roads):
    visited = set()
    queue = deque()
    queue.append((start_signs, 0))
    visited.add(start_signs)

    while queue:
        current, steps = queue.popleft()
        if current == end_signs:
            return steps
        for i in range(len(roads)):
            new_signs = list(current)
            new_signs[i] *= -1
            new_signs = tuple(new_signs)
            if new_signs not in visited:
                visited.add(new_signs)
                queue.append((new_signs, steps + 1))
    return -1

def main():
    x1, y1, x2, y2, roads = read_input()
    start = get_signs((x1, y1), roads)
    end = get_signs((x2, y2), roads)
    print(bfs(start, end, roads))

