    from collections import defaultdict
    len_to_strings = defaultdict(list)
    for s in strings:
        len_to_strings[len(s)].append(s)

    # Find the two strings of length n-1
    candidates = len_to_strings[n-1]

    # Try each possible combination of prefix and suffix candidates
    for i in range(2):
        for j in range(2):
            if i == j:
                continue
            prefix_candidate = candidates[i]
            suffix_candidate = candidates[j]
            # Try to reconstruct s
            # Case 1: prefix_candidate + last char of suffix_candidate
            s1 = prefix_candidate + suffix_candidate[-1]
            # Case 2: first char of prefix_candidate + suffix_candidate
            s2 = prefix_candidate[0] + suffix_candidate
            # Check if s1 or s2 is valid
            for s in [s1, s2]:
                valid = True
                # Check all prefixes and suffixes of s
                for l in range(1, n):
                    prefix = s[:l]
                    suffix = s[-l:]
                    # Check if both prefix and suffix are present in the input
                    count_prefix = 0
                    count_suffix = 0
                    for st in strings:
                        if len(st) == l:
                            if st == prefix:
                                count_prefix += 1
                            if st == suffix:
                                count_suffix += 1
                    if count_prefix < 1 or count_suffix < 1:
