    # If the current position is already within [l, r], we only need to close tabs outside
    if l <= pos <= r:
        # Close tabs to the left of l
        if pos > l:
            # Move from pos to l and close
            return (pos - l) + 1
        # Close tabs to the right of r
        elif pos < r:
            # Move from pos to r and close
            return (r - pos) + 1
        else:
            # Already at l or r, just close one side
            if l > 1:
                return 1
            elif r < n:
                return 1
            else:
                return 0
    else:
        # Need to move to the segment [l, r] first
        # Decide whether to move left or right
        if pos < l:
            # Move from pos to l and close all to the left
            return (l - pos) + 1
        elif pos > r:
            # Move from pos to r and close all to the right
            return (pos - r) + 1
        else:
            # This should not happen as we checked earlier
            return 0

