import sys

def min_seconds_to_close_tabs(n, pos, l, r):
    # If the current tabs are already the desired range, do nothing
    if l == 1 and r == n:
        return 0

    # Determine which tabs need to be closed
    left_closed = l > 1
    right_closed = r < n

    # If only left tabs need to be closed
    if not right_closed:
        # Move to l and close all to the right
        if pos <= l:
            return (l - pos) + 1
        else:
            return (pos - l) + 1

    # If only right tabs need to be closed
    if not left_closed:
        # Move to r and close all to the left
        if pos >= r:
            return (pos - r) + 1
        else:
            return (r - pos) + 1

    # Both left and right tabs need to be closed
    # Option 1: Close left first then move to r and close right
    time1 = (pos - l) + 1 + (r - l)
    # Option 2: Close right first then move to l and close left
    time2 = (r - pos) + 1 + (r - l)
    return min(time1, time2)

