    prefix_21 = [0] * (n)
    prefix_12 = [0] * (n)
    for i in range(1, n):
        prefix_21[i] = prefix_21[i-1] + trans_21[i-1]
        prefix_12[i] = prefix_12[i-1] + trans_12[i-1]

    B = prefix_21[n-1]  # total number of 2->1 transitions

    min_delta = float('inf')

    for l in range(1, n+1):
        for r in range(l, n+1):
            l_start = l - 1
            r_end = r - 1
            # Calculate c_21 and c_12
            if l_start >= r_end:
                c_21 = 0
                c_12 = 0
            else:
                c_21 = prefix_21[r_end] - prefix_21[l_start]
                c_12 = prefix_12[r_end] - prefix_12[l_start]
            delta_inside = c_12 - c_21

            # Calculate e_old
            e_old = 0
            # left_old
            if l > 1:
                if a[l-2] == 2 and a[l-1] == 1:
                    e_old += 1
            # right_old
            if r < n:
                if a[r-1] == 2 and a[r] == 1:
                    e_old += 1

            # Calculate e_new
            e_new = 0
            # left_new
            if l > 1:
                if a[l-2] == 2 and a[r_end] == 1:
                    e_new += 1
            # right_new
            if r < n:
                if a[l_start] == 2 and a[r] == 1:
                    e_new += 1

            delta_edges = e_new - e_old
            total_delta = delta_inside + delta_edges

            if total_delta < min_delta:
                min_delta = total_delta

