def main():
    import sys
    input = sys.stdin.read().split()
    idx = 0
    n = int(input[idx])
    idx += 1
    a = list(map(int, input[idx:idx + n]))

    # Precompute prefix_ones[i] = number of ones in a[0..i-1]
    prefix_ones = [0] * (n + 1)
    for i in range(n):
        prefix_ones[i + 1] = prefix_ones[i] + (1 if a[i] == 1 else 0)

    # Precompute suffix_twos[i] = number of twos in a[i..n-1]
    suffix_twos = [0] * (n + 1)
    for i in range(n - 1, -1, -1):
        suffix_twos[i] = suffix_twos[i + 1] + (1 if a[i] == 2 else 0)

    max_lnds = 0

    # Original case
    total_ones = prefix_ones[n]
    total_twos = suffix_twos[0]
    # Compute original lnds
    # Find the last occurrence of 1
    last_one = -1
    for i in range(n):
        if a[i] == 1:
            last_one = i
    if last_one == -1:
        max_lnds = total_ones
    else:
        twos_after_last_one = suffix_twos[last_one + 1]
        max_lnds = total_ones + twos_after_last_one

    # Now consider all possible reversals
    for l in range(n):
        for r in range(l, n):
            # Compute the effect of reversing [l, r]
            # We need to find the new last one in the array after reversal
            # To do this, we need to know:
            # 1. the original last one before l
            # 2. the last one in the reversed subarray
            # 3. the original last one after r

            # original last one before l
            pre_last_one = -1
            for i in range(l):
                if a[i] == 1:
                    pre_last_one = i

            # original last one after r
            post_last_one = -1
            for i in range(r + 1, n):
                if a[i] == 1:
                    post_last_one = i

            # last one in the reversed subarray
            # which is the first occurrence of 1 in the original subarray when scanned from right to left
            rev_sub_last_one = -1
            for i in range(r, l - 1, -1):
                if a[i] == 1:
                    rev_sub_last_one = i
                    break

            new_last_one = -1
            if pre_last_one != -1:
                new_last_one = pre_last_one
            if rev_sub_last_one != -1:
                if new_last_one == -1 or rev_sub_last_one > new_last_one:
                    new_last_one = rev_sub_last_one
            if post_last_one != -1:
                if new_last_one == -1 or post_last_one > new_last_one:
                    new_last_one = post_last_one

            if new_last_one == -1:
                # all are 2s
                current = total_ones
            else:
                twos_after_new_last = suffix_twos[new_last_one + 1]
                current = total_ones + twos_after_new_last

            if current > max_lnds:
                max_lnds = current

    print(max_lnds)

