import sys

def max_non_decreasing_subsequence_length(n, a):
    # Precompute dp arrays for longest non-decreasing subsequence
    # dp[i] = length of longest non-decreasing subsequence ending at i
    dp = [1] * n
    for i in range(n):
        for j in range(i):
            if a[j] <= a[i]:
                dp[i] = max(dp[i], dp[j] + 1)

    # Reverse the array and compute dp again
    a_rev = a[::-1]
    dp_rev = [1] * n
    for i in range(n):
        for j in range(i):
            if a_rev[j] <= a_rev[i]:
                dp_rev[i] = max(dp_rev[i], dp_rev[j] + 1)

    # Now consider all possible intervals [l, r] to reverse
    max_len = 0
    for l in range(n):
        for r in range(l, n):
            # Get the reversed segment
            rev_segment = a[l:r+1][::-1]
            # Combine prefix before l, reversed segment, and suffix after r
            new_a = a[:l] + rev_segment + a[r+1:]

            # Compute longest non-decreasing subsequence for new_a
            curr_len = 1
            for i in range(1, len(new_a)):
                if new_a[i] >= new_a[i-1]:
                    curr_len += 1
                else:
                    curr_len = 1
            max_len = max(max_len, curr_len)

    return max_len

