MOD = 10**9 + 7

def precompute_factorials(max_n):
    fact = [1] * (max_n + 1)
    for i in range(1, max_n + 1):
        fact[i] = fact[i-1] * i % MOD
    return fact

def comb(n, k, fact):
    if k < 0 or k > n:
        return 0
    return fact[n] * pow(fact[k] * fact[n - k], MOD - 2, MOD) % MOD

def main():
    import sys
    input = sys.stdin.read().split()
    idx = 0
    cases = []
    while idx < len(input):
        n = int(input[idx])
        m = int(input[idx+1])
        g = int(input[idx+2])
        cases.append((n, m, g))
        idx += 3

    max_nm = 0
    for n, m, _ in cases:
        max_nm = max(max_nm, n + m)

    fact = precompute_factorials(max_nm)

    results = []
    for n, m, g in cases:
        total = n + m
        if total == 0:
            results.append(0)
            continue

        # The final character depends on the parity of the number of 0s
        # If the number of 0s is even, final is 0; else 1
        if (n % 2) == g:
            c = comb(n + m, n, fact)
            results.append(c)
        else:
            results.append(0)

    for res in results:
        print(res)

