def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    A = data[1]

    # Memoization cache
    memo = {}

    @functools.lru_cache(maxsize=None)
    def dp(homer_turns, marge_turns, idx):
        if idx == 2 * n:
            return (0, "")

        # Current digit
        digit = int(A[idx])

        # If Homer needs to take a turn
        if homer_turns < n:
            # Take this digit for Homer
            val = digit * (10 ** (n - 1 - homer_turns))
            res, seq = dp(homer_turns + 1, marge_turns, idx + 1)
            total = val + res
            return (total, 'H' + seq)

        # If Marge needs to take a turn
        if marge_turns < n:
            # Take this digit for Marge
            val = digit * (10 ** (n - 1 - marge_turns))
            res, seq = dp(homer_turns, marge_turns + 1, idx + 1)
            total = val + res
            return (total, 'M' + seq)

        return (0, "")

    _, result = dp(0, 0, 0)
    print(result)

if __name__ == "__main__":
    main()

